    private void cancelButtonActionPerformed(java.awt.event.ActionEvent evt) {
        this.dispose();
    }

    private void saveButtonActionPerformed(java.awt.event.ActionEvent evt) {
        if(this.projectName.getText()==null || this.projectName.getText().isEmpty()){
            JOptionPane.showMessageDialog(null, "Please enter non empty project name");
        }else{
            String projectName = this.projectName.getText();
            try{
                AiravataManager.createNewProject(projectName);
                JOptionPane.showMessageDialog(null, "Project created successfully");
            }catch(Exception ex){
                ex.printStackTrace();
                JOptionPane.showMessageDialog(null, "Project creation failed. Please try again later");
            }
            this.dispose();
        }
    }

